import React from 'react';
import styled from 'styled-components';
import logo from '../assets/color_logo.png'
import Input from "./Input";

const InitialInputPane = () => {
    return (
        <Container>
            <LogoWrapper>
                <img src={logo} alt=""/>

            </LogoWrapper>
            <Form>
                <h3>Seller Portal</h3>
                <h2></h2>
                <h2>List Collectible</h2>
                <h2>Bounded F21 Collection</h2>
                <h2></h2>

                <Input placeholder="Item Name"/>
                <Input placeholder="Token Symbol"/>
                <Input placeholder="Units Available" type="number"/>

                <select name="Deployment Network" id="networks">
                    <option value="mainnet">Ethereum Main Network</option>
                    <option value="ropsten">Ropsten Test Network</option>
                    <option value="rinkeby">Rinkeby Test Network</option>
                    <option value="kovan">Kovan Test Network</option>
                    <option value="goerli">Goerli Test Network</option>
                </select>

                <h1>
                    Deployment Fee:
                    <span>
                    &nbsp;somevalue
                    </span>
                </h1>

                <button> Tokenize Unit Supply </button>
            </Form>
        </Container>
    )
}

const Form = styled.form`
    width : 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    h3 {
        font-weight: 700;
        margin-top: 2rem;
        margin-bottom: 2rem;
        text-align: center;
        font-size: 30px;
    }
    h2 {
        font-weight: 600;
        margin-bottom: 1rem;
        text-align: center;
        font-size: 20px;
    }
    h1 {
        font-weight: 300;
        margin-bottom: 1rem;
        text-align: center;
        font-size: 16px;
    }
    select {
        width : 90%;
        max-width: 376px;
        height: 40px;
        border: none;
        margin: 0.5rem 0;
        background-color: #f5f5f5;
        border-radius: 8px;
        padding: 0 1rem;


        margin-bottom: 1rem;

        display: block;
        margin-left: auto;
        margin-right: auto;
    }
    span {
        color: #5dc399;
    }
    button {
        width: 75%;
        max-width: 350px;
        height: 40px;
        border: none;
        margin: 1rem 0;
        border-radius: 8px;
        background-color: #70EDB9;
        color: #ffffff;
        font-weight: 600;
        cursor: pointer;
        transition: all 0.2s ease-in;
        &:hover{
            transform: translateY(-3px);
        }
    }
`;

const LogoWrapper = styled.div`
img {
    height: 3rem;
    display: block;
    margin-left: auto;
    margin-right: auto;
}

h3 {
    text-align: center;
    font-weight: 300;
    font-size: 18px
}
margin-top: 1rem;
margin-bottom: 1rem;

`;

const Container = styled.div`
    width : 60%;
    margin: 0 auto;
    backdrop-filter : blur(135px);
    background-color : rgba(255, 255, 255, 0.1);
    height: 100%; 
    display: flex;
    flex-direction: column;
    justify-content: normal;
    align-items: center;
    padding: 0 2rem;

    @media (max-width : 900px) {
        width: 100vw;
        position: absolute;
        padding: 0;
    }
`;

export default InitialInputPane;
